import {Howl,Howler} from 'howler'
import {useEffect, useState} from 'react'
import {useRef} from "react/cjs/react.production.min";

const Pad = (props) => {

    let {sound,songPlay,index,songPause} = props;


    const [songState , setSongState] = useState('play');

    const changePadState = (state) => {


            if (state === 'play') {


                if(songPlay.length === 0) {
                    songPlay.push(sound);
                    sound.play();

                }
                else{

                    songPlay.push(sound);

                }
                setSongState('stop')
            } else {

                sound.stop();
                songPlay.shift();
                setSongState('play');
            }

    }
    useEffect(()=>{
        console.log('hi')
       if(songPause === true){
           sound.pause()
       }
    },[songPause])





    return(

        <button onClick={()=> changePadState(songState)}>
            shalom


        </button>
    )

    }

    export default Pad;
