import {useEffect, useState,useRef} from 'react'


const Pad = (props) => {

    let {sound,songPlay,songPause} = props;


    const [songState , setSongState] = useState('play');
    const [songPause2,setSongPause] = useState(false);

    //variable to check if compononted mounted to the DOM
    const isMount = useRef(false);



    useEffect(()=>{

        if(isMount.current === true) {
            if (songPause === true) {

                sound.pause();
                

            } else {
                if(songPause2 === true) {
                    sound.play();
                }
            }
        }
        else{
            isMount.current = true;
        }
    },[songPause])

    const changePadState = (state) => {


            if (state === 'play') {


                if(songPlay.length === 0) {

                    songPlay.push(sound);
                    sound.play();


                }
                else{

                    songPlay.push(sound);

                }
                setSongState('stop')
            } else {

                sound.stop();
                setSongPause(false);
                songPlay.shift();
                setSongState('play');
            }

    }






    return(

        <button style={{margin:'3rem',padding:'1rem'}} onClick={() => changePadState(songState)}>
            shalom


        </button>
    )

    }

    export default Pad;
