import {Howl,Howler} from 'howler'
import {useEffect, useState} from 'react'

const Pad = (props) => {
let {src} = props;
const sound = new Howl({src : [src]});
const [songState , setSongState] = useState('play');
const changePadState = (state) => {
    if(state === 'play'){
        setSongState('stop')
    }
    else{
        setSongState('play');
    }
}
useEffect(()=>{
    console.log(songState)
        if(songState === 'play'){
            soundPlay(sound);
        }
        else{
            soundStop(sound);
        }
},[songState])

const soundPlay = (sound) =>{

    sound.play();
}

const soundStop = (sound) => {
    sound.stop();
}

Howler.volume(1.0)
return(

    <button onClick={()=> changePadState(songState)}>

    </button>
)

}

export default Pad;
