import {Howl,Howler} from 'howler'
import {useEffect, useState} from 'react'
import {useRef} from "react/cjs/react.production.min";

const Pad = (props) => {

    let {sound,songPlay,index} = props;


    const [songState , setSongState] = useState('play');

    const changePadState = (state) => {
        let obj = {
            sound
        };

            if (state === 'play') {
                if(songPlay.length === 0) {
                    songPlay.push(sound);
                    sound.play();
                    obj.state = 'play';
                }
                else if(songPlay.forEach()){
                    obj.state = 'stop';
                    songPlay.push(obj);

                }
                setSongState('stop')
            } else {
                console.log('stop')
                sound.stop();
                obj.state = 'stop';
                setSongState('play');
            }

    }





    return(

        <button onClick={()=> changePadState(songState)}>
            shalom


        </button>
    )

    }

    export default Pad;
