import {Howl,Howler} from 'howler'
import {useEffect, useState} from 'react'
import {useRef} from "react/cjs/react.production.min";

const Pad = (props) => {

    let {sound,setSongPlay,songPlay,index} = props;


    const [songState , setSongState] = useState('play');
    const changePadState = (state) => {

            if (state === 'play') {
                if(songPlay.length === 0) {
                    setSongPlay(songPlay.push(index));
                    sound.play();
                }
                else{
                    setSongPlay(songPlay.push(index));
                }
                setSongState('stop')
            } else {
                console.log('stop')
                sound.stop()
                setSongState('play');
            }

    }





    return(

        <button onClick={()=> changePadState(songState)}>
            shalom


        </button>
    )

    }

    export default Pad;
