import {useEffect, useState} from 'react'

const Pad = (props) => {
    
let {src} = props;

const [songState , setSongState] = useState('play');
const changePadState = (state) => {
    if(state === 'play'){
        setSongState('stop')
    }
    else{
        setSongState('play');
    }
}
useEffect(()=>{
    console.log(songState)
        if(songState === 'play'){
            soundGeneral(src,songState);
        }
        else{
            console.log('hi')
            soundGeneral(src,songState);
        }
},[songState])
const soundGeneral = (src,state) => {
    const sound = new Howl({src : [src]});
    if(state === 'play') {
        sound.play();
    }
    else{
         sound.stop()
    }
    }




return(

    <button onClick={()=> changePadState(songState)}>
        shalom


    </button>
)

}

export default Pad;
