const Pad = (props) => {
let {src} = props
const soundPlay = (src) =>{
    const sound = new Howl({src : [src]});
    sound.play();
}
Howler.volume(1.0)
return(

    <button onClick={()=> soundPlay(src)}>

    </button>
)

}

export default Pad;
