import {useEffect, useState,useRef} from 'react'


const Pad = (props) => {

    let {sound,songPlay,songPause,number} = props;


    const [songState , setSongState] = useState('play');
    const [songPause2,setSongPause] = useState(false)
    const isMount = useRef(false);



    useEffect(()=>{

        if(isMount.current===true) {
            if (songPause === true) {

                sound.pause();

            } else {
                if(songPause2 === true) {
                    sound.play();
                }
            }
        }
        else{
            isMount.current = true;
        }
    },[songPause])

    const changePadState = (state) => {


            if (state === 'play') {


                if(songPlay.length === 0) {
                    console.log(sound)
                    songPlay.push(sound);
                    sound.play();
                    setSongPause(true)

                }
                else{

                    songPlay.push(sound);

                }
                setSongState('stop')
            } else {
                console.log('hi')
                sound.stop();
                setSongPause(false);
                songPlay.shift();
                setSongState('play');
            }

    }






    return(

        <button onClick={()=> changePadState(songState)}>
            shalom


        </button>
    )

    }

    export default Pad;
