import {Howl,Howler} from 'howler'
import {useEffect, useState} from 'react'
import {useRef} from "react/cjs/react.production.min";

const Pad = (props) => {

    let {sound} = props;


    const [songState , setSongState] = useState('play');
    const changePadState = (state) => {

            if (state === 'play') {
                sound.loop();
                setSongState('stop')
            } else {
                console.log('stop')
                sound.stop()
                setSongState('play');
            }

    }





    return(

        <button onClick={()=> changePadState(songState)}>
            shalom


        </button>
    )

    }

    export default Pad;
